onMounted(async () => {
  const send = {
    headers: { "Content-type": "application/json", credentials: "include" },
    method: "POST",
    body: JSON.stringify({
      username: data,
    }),
  };
  myUser.value = await fetch("http://127.0.0.1:8000/api/showOneUser", send)
    .then(value => {
      if (value.status === 200) {
        return value.json()
      }
    })
});


